import React from 'react';
import { Box, Text, Heading } from 'theme-ui';

export default function SectionHeader({ title, slogan, isWhite }) {
  //dynamic so we can change everytime, no duplicating code
  return (
    <Box sx={{variant:'sectionHeader'}}>
      <Text as="p" sx={{variant: 'sectionHeader.subTitle', color: isWhite? 'white':'primary', opacity: isWhite? 0.7: 1,}}>
      {slogan}
      </Text>
      <Heading
      as="h2"
      sx={{variant: 'sectionHeader.subTitle', color: isWhite? 'white':'primary', opacity: isWhite? 0.7: 1,}}
      >

      </Heading>
    </Box>

  );
}
